import _ from 'lodash';
import { CONST_LAYER_TYPES, TypeVectorLayerStyles } from '@/geo/layer/geoview-layers/abstract-geoview-layers';
import { TypeLegendLayer, TypeLegendItem } from '@/core/components/layers/types';
import { useGeoViewStore } from '@/core/stores/stores-managers';
import { generateId } from '@/core/utils/utilities';
import { MapEventProcessor } from '@/api/event-processors/event-processor-children/map-event-processor';

// ? I doubt we want to define an explicit type for this helper?
export function useLegendHelpers(): unknown {
  const store = useGeoViewStore();

  const { mapId } = store.getState();

  function populateLegendStoreWithFakeData(): void {
    const layerItems: TypeLegendItem[] = [
      {
        geometryType: 'Point',
        name: 'item 1',
        isVisible: true,
        icon: 'data:image/png;base64,',
      },
      {
        geometryType: 'Point',
        name: 'item 2',
        isVisible: true,
        icon: 'data:image/png;base64,',
      },
      {
        geometryType: 'Point',
        name: 'item 3',
        isVisible: false,
        icon: 'data:image/png;base64,',
      },
      {
        geometryType: 'Point',
        name: 'item 4',
        isVisible: true,
        icon: 'data:image/png;base64,',
      },
      {
        geometryType: 'Point',
        name: 'item 5',
        isVisible: true,
        icon: 'data:image/png;base64,',
      },
      {
        geometryType: 'Point',
        name: 'item 6',
        isVisible: true,
        icon: 'data:image/png;base64,',
      },
    ];
    const legendLayers: TypeLegendLayer[] = [
      {
        bounds: undefined,
        layerId: 'test_testLayerId',
        layerPath: 'testLayerPath',
        layerName: 'TEST--TestLayer1',
        type: CONST_LAYER_TYPES.GEOJSON,
        layerStatus: 'loaded',
        legendQueryStatus: 'queried',
        querySent: true,
        children: [],
        items: layerItems,
        icons: [],
      },
      {
        bounds: undefined,
        layerId: 'test_geojsonLYR5',
        layerPath: 'geojsonLYR5',
        layerName: 'TEST--Layer with groups',
        type: CONST_LAYER_TYPES.GEOJSON,
        layerStatus: 'loaded',
        legendQueryStatus: 'queried',
        querySent: true,
        children: [],
        items: [],
        icons: [],
      },
      {
        bounds: undefined,
        layerId: 'test_chrisLayr1',
        layerPath: 'Chris Sample Parent1',
        layerName: 'TEST--chrisparentlayer1',
        type: CONST_LAYER_TYPES.GEOJSON,
        layerStatus: 'loaded',
        legendQueryStatus: 'queried',
        querySent: true,
        children: [
          {
            bounds: undefined,
            layerId: 'test_chrisChildLayer1',
            layerPath: 'ChrisSampleC3',
            layerName: 'TEST--chrisparentchild1',
            type: CONST_LAYER_TYPES.GEOJSON,
            layerStatus: 'loaded',
            legendQueryStatus: 'queried',
            querySent: true,
            children: [
              {
                bounds: undefined,
                layerId: 'test_chrisGrandChild1',
                layerPath: 'Gran ChildChidl2',
                layerName: 'TEST--chris parent child2',
                type: CONST_LAYER_TYPES.GEOJSON,
                layerStatus: 'error',
                legendQueryStatus: 'init',
                querySent: true,
                children: [],
                items: layerItems,
                icons: [],
              },
              {
                bounds: undefined,
                layerId: 'test_chrisGrandChild5',
                layerPath: 'Gran ChildChidl555',
                layerName: 'TEST--chris parent child25555',
                type: CONST_LAYER_TYPES.GEOJSON,
                layerStatus: 'processing',
                legendQueryStatus: 'init',
                querySent: true,
                children: [],
                items: layerItems,
                icons: [],
              },
              {
                bounds: undefined,
                layerId: 'test_chrisGrandChild2',
                layerPath: 'Grand Child Chidl3',
                layerName: 'TEST--chris parent child3',
                type: CONST_LAYER_TYPES.GEOJSON,
                layerStatus: 'newInstance',
                legendQueryStatus: 'init',
                querySent: true,
                children: [],
                items: layerItems,
                icons: [],
              },
            ],
            items: [],
            icons: [],
          },
          {
            bounds: undefined,
            layerId: 'test_chrisChild2',
            layerPath: 'Chris Sample Chidl2',
            layerName: 'TEST--chirslyerss',
            type: CONST_LAYER_TYPES.GEOJSON,
            layerStatus: 'loaded',
            legendQueryStatus: 'queried',
            querySent: true,
            children: [],
            items: layerItems,
            icons: [],
          },
          {
            bounds: undefined,
            layerId: 'test_chrisChild3',
            layerPath: 'Chris Sample Chidl3',
            layerName: 'TEST--chris-child32edd',
            type: CONST_LAYER_TYPES.GEOJSON,
            layerStatus: 'loaded',
            legendQueryStatus: 'queried',
            querySent: true,
            children: [],
            items: layerItems,
            icons: [],
          },
        ],
        items: [],
        icons: [],
      },
    ];

    const legendInfo = MapEventProcessor.getMapViewerLayerAPI(mapId).legendsLayerSet.resultSet;
    const keys = _.keys(legendInfo);

    keys.forEach((i) => {
      const setData = legendInfo[i];
      const items: TypeLegendItem[] = [];
      const legendData = setData.data?.legend ? (setData.data.legend as TypeVectorLayerStyles) : undefined;
      const itemCanvases = legendData ? legendData.Point?.arrayOfCanvas : undefined;
      if (itemCanvases) {
        itemCanvases.forEach((r, ind) => {
          if (r) {
            items.push({
              geometryType: 'Point',
              name: `TEST--Item name ${ind}`,
              isVisible: true,
              icon: r.toDataURL(),
            });
          }
        });
      }

      const item: TypeLegendLayer = {
        bounds: undefined,
        layerId: `layer${i}`,
        layerPath: `test_${generateId()}`,
        layerName: `TEST---Unknown Layer name`,
        type: setData.data?.type ?? CONST_LAYER_TYPES.IMAGE_STATIC,
        layerStatus: setData.layerStatus,
        legendQueryStatus: 'queried',
        children: [],
        items,
        icons: [],
      };

      if (i.startsWith('geojsonLYR5')) {
        legendLayers[1].children.push(item);
      } else {
        legendLayers.push(item);
      }
    });

    // adding to store
    store.setState({
      layerState: {
        ...store.getState().layerState,
        legendLayers: [...store.getState().layerState.legendLayers, ...legendLayers],
      },
    });
  }

  return {
    populateLegendStoreWithFakeData,
  };
}
